<?php//Contiene las variables de configuracion para conectar a la base de datosrequire_once ("../conf/confconexion.php");//Contiene funcion que conecta a la base de datos$id=$_POST['id_p'];if($id==0){    $titulo="Nuevo Medicina";}if($id>0){    $titulo="Editar Medicina";    $sql="select * from tb_medicina where id_medicina=$id";    $result= mysqli_query($objConexion, $sql);    if($result!=null){        if(mysqli_num_rows($result)>0){            $usuarioA= mysqli_fetch_array($result);            $NombresMedic=$usuarioA['NombreMedicina'];            $geniaMedic=$usuarioA['NombreGenerico'];            $categMedic=$usuarioA['id_categoria'];                     $estadoMedico=$usuarioA['estado'];            if($estadoMedico=='1'){                $seleccionaA="selected";                $seleccionaI="";            }            if($estadoMedico=='0'){                $seleccionaI="selected";                $seleccionaA="";            }        }else{            echo "No se encontró registro con el código: ".$id;            exit();        }    }else{        echo "Ocurrió un problema al momento de ejecutar la consulta".mysqli_error_list();        exit();    }}?><script>$(document).ready(function(){    // capturar el valor del id que se recibe    $('#IdUsuario').val(<?php echo $id; ?>);     $('#guardar_usuario').bind("submit", function (){        //alert(123);       $.ajax({           type: $(this).attr("method"),           url:'ajax/grabar_medicinas.php',           data:$(this).serialize(),           success: function (data){               $("#resultados_usuario").html(data);               listar('ajax/listar_medicinas.php');           }       });        return false;    });});</script><html><div class="modal fade" id="MyModal">    <div class="modal-dialog" role="document">        <div class="modal-content">            <div class="modal-header"> <h4 class="