<?php//Contiene las variables de configuracion para conectar a la base de datosrequire_once ("../conf/confconexion.php");//Contiene funcion que conecta a la base de datos$id=$_POST['id_p'];if($id==0){    $titulo="Nuevo Usuario";}if($id>0){    $titulo="Editar Usuario";    $sql="select * from tb_usuario where id_usuario=$id";    $result= mysqli_query($objConexion, $sql);    if($result!=null){        if(mysqli_num_rows($result)>0){            $usuarioA= mysqli_fetch_array($result);            $NombresPersona=$usuarioA['nombre'];            $usuarioPersona=$usuarioA['correo'];          ;            $idrolus=$usuarioA['id_tipo_usuario'];                       $estadoPersona=$usuarioA['estado'];            if($estadoPersona=='1'){                $seleccionaA="selected";                $seleccionaI="";            }            if($estadoPersona=='0'){                $seleccionaI="selected";                $seleccionaA="";            }        }else{            echo "No se encontr칩 registro con el c칩digo: ".$id;            exit();        }    }else{        echo "Ocurri칩 un problema al momento de ejecutar la consulta".mysqli_error_list();        exit();    }}?><script>$(document).ready(function(){    // capturar el valor del id que se recibe    $('#IdUsuario').val(<?php echo $id; ?>);     $('#guardar_usuario').bind("submit", function (){        //alert(123);       $.ajax({           type: $(this).attr("method"),           url:'ajax/grabar_usuario.php',           data:$(this).serialize(),           success: function (data){               $("#resultados_usuario").html(data);               listar('ajax/listar_usuario.php');           }       });        return false;    });});</script><html><div class="modal fade" id="MyModal">    <div class="modal-dialog" role="document">        <div class="modal-content">            <div class="modal-header">                <h4 class="modal-title" id="myModalLabel"><i class='fa fa-edit'></i> <?php echo $titulo; ?></h4>                <button type="button" class="close" data-dismiss="modal" aria-label="Close"><span aria-hidden="true">&times;</span></button>                            </div>           <div class="modal-body">                <form class="form-horizontal" method="post" id="guardar_usuario" name="guardar_usuario">                                     <div class="input-group mb-2">                    <div class="input-group-prepend">                        <span class="input-group-text">                            <i class="fa fa-user"></i>                        </span>                    </div>                                                                    <input id="Nombres" placeholder="Nombres y Apellidos" name="Nombres_txt" class="form-control" value="<?php echo $NombresPersona; ?>" required/>                                            </div>                                       <div class="input-group mb-2">                    <div class="input-group-prepend">                        <span class="input-group-text">                            <i class="fa fa-at"></i>                        </span>                    </div>                        <input type="email" placeholder="Correo" id="UsuarioId" name="correo_txt" class="form-control" value="<?php echo $usuarioPersona; ?>" required/>                        </div>                                      <?php                   if($id==0){                                                             ?>                  <div class="input-group mb-2">                    <div class="input-group-prepend">                        <span class="input-group-text">                            <i class="fa fa-lock"></i>                        </span>                    </div>                                             <input id="ClaveId"  placeholder="Contrase침a"name="Clave_txt" class="form-control" value="<?php echo $clavePersona; ?>" required/>                        </div>                  <?php } ?>                                   <div class="input-group mb-2">                    <div class="input-group-prepend">                        <span class="input-group-text">                            <i class="fa fa-users"></i>                        </span>                    </div>                         <select class="custom-select" id="rol" name="rol_txt" required>                             <option value="">Seleccione Rol</option>                             <?php                              $sql_rol="select * from tb_tipo_usuario ;";                             $result_rol= mysqli_query($objConexion, $sql_rol);                             while($rolA= mysqli_fetch_array($result_rol)){                                 $NombresRol=$rolA['descripcion'];                                 $idcanton=$rolA['id_tipo_usuario'];                                 $seleccionaCanton='';                                    if($idcanton==$idrolus){                                        $seleccionaCanton='selected';                                    }                                 ?>                                <option value="<?php echo $idcanton; ?>" <?php echo $seleccionaCanton; ?>><?php echo $NombresRol; ?></option>                                <?php                             }                             ?>                                                                                   </select>                        </div>                                       <div class="input-group mb-2">                    <div class="input-group-prepend">                        <span class="input-group-text">                            <i class="fa fa-check-double"></i>                        </span>                    </div>                                                <select class="custom-select" id="estado" name="estado_txt" required>                             <option value="">Seleccione Estado</option>                             <option value="1" <?php echo $seleccionaA; ?>>Activo</option>                             <option value="0" <?php echo $seleccionaI; ?>>Inactivo</option>                          </select>                        </div>                                                            <div class="modal-footer">                <button type="button" class="btn btn-danger" data-dismiss="modal">Cerrar</button>                <button type="submit" class="btn btn-success" id="guardar_datos">Guardar Datos</button>            </div>                     <div id="resultados_usuario"></div>                     <input id="IdUsuario" name="IdUsuario" type="hidden">                </form>            </div>                    </div>    </div></div></html>